import React from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useAuth0 } from '@auth0/auth0-react';
import {
  Box,
  Container,
  Paper,
  Typography,
  Button,
  TextField,
  Stepper,
  Step,
  StepLabel,
  Stack,
  RadioGroup,
  FormControlLabel,
  Radio,
  FormControl,
  Select,
  MenuItem,
  InputLabel,
  Alert,
  Grid,
  Chip,
  IconButton,
} from '@mui/material';
import {
  ArrowBack,
  ArrowForward,
  Person,
  Business,
  Check,
} from '@mui/icons-material';
import axios from 'axios';
import toast from 'react-hot-toast';

const Register = () => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const { loginWithRedirect, user, isAuthenticated, getAccessTokenSilently } = useAuth0();

  const [activeStep, setActiveStep] = React.useState(0);
  const [userType, setUserType] = React.useState(searchParams.get('type') || 'customer');
  const [formData, setFormData] = React.useState({
    // Common data
    full_name: '',
    phone: '',

    // Customer data
    preferences: [],

    // Provider data
    business_name: '',
    service_category: '',
    description: '',
    vat_number: '',
    address: {
      street: '',
      city: '',
      postal_code: '',
      province: '',
    },
    services_offered: [],
  });

  const [error, setError] = React.useState('');
  const [loading, setLoading] = React.useState(false);

  const steps = ['Tipo Account', 'Dati Personali', 'Dettagli', 'Conferma'];

  const serviceCategories = [
    { value: 'restaurant', label: 'Ristorante/Bar' },
    { value: 'beauty', label: 'Bellezza/Benessere' },
    { value: 'health', label: 'Salute/Medicina' },
    { value: 'professional', label: 'Servizi Professionali' },
    { value: 'home_services', label: 'Servizi Casa' },
    { value: 'shop', label: 'Negozio' },
    { value: 'other', label: 'Altro' },
  ];

  React.useEffect(() => {
    if (isAuthenticated && user) {
      setFormData(prev => ({
        ...prev,
        full_name: user.name || '',
      }));
    }
  }, [isAuthenticated, user]);

  const handleNext = () => {
    if (activeStep === 0 && !isAuthenticated) {
      loginWithRedirect({
        screen_hint: 'signup',
        appState: { returnTo: '/register', step: 1, userType }
      });
      return;
    }

    if (activeStep === steps.length - 1) {
      handleSubmit();
    } else {
      setActiveStep((prev) => prev + 1);
    }
  };

  const handleBack = () => {
    setActiveStep((prev) => prev - 1);
  };

  const handleSubmit = async () => {
    try {
      setLoading(true);
      const token = await getAccessTokenSilently();

      const payload = {
        user_type: userType,
        full_name: formData.full_name,
        phone: formData.phone,
        ...(userType === 'provider' && {
          business_name: formData.business_name,
          service_category: formData.service_category,
          description: formData.description,
          vat_number: formData.vat_number,
          address: formData.address,
          services_offered: formData.services_offered,
        }),
      };

      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/auth/register`,
        payload,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.data.success) {
        localStorage.setItem('user_data', JSON.stringify(response.data.user));
        toast.success('Registrazione completata con successo!');
        navigate('/dashboard');
      }
    } catch (err) {
      setError(err.response?.data?.detail || 'Errore durante la registrazione');
    } finally {
      setLoading(false);
    }
  };

  const renderStepContent = () => {
    switch (activeStep) {
      case 0:
        return (
          <Box>
            <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>
              Scegli il tipo di account
            </Typography>
            <RadioGroup
              value={userType}
              onChange={(e) => setUserType(e.target.value)}
            >
              <Paper sx={{ 
                p: 3, 
                mb: 2, 
                cursor: 'pointer', 
                border: userType === 'customer' ? '2px solid' : '1px solid', 
                borderColor: userType === 'customer' ? 'primary.main' : 'grey.300' 
              }}>
                <FormControlLabel
                  value="customer"
                  control={<Radio />}
                  label={
                    <Box sx={{ ml: 1 }}>
                      <Stack direction="row" alignItems="center" spacing={1}>
                        <Person />
                        <Typography variant="h6">Cliente</Typography>
                      </Stack>
                      <Typography variant="body2" color="text.secondary">
                        Cerca e prenota servizi nella tua zona
                      </Typography>
                    </Box>
                  }
                />
              </Paper>

              <Paper sx={{ 
                p: 3, 
                cursor: 'pointer', 
                border: userType === 'provider' ? '2px solid' : '1px solid', 
                borderColor: userType === 'provider' ? 'primary.main' : 'grey.300' 
              }}>
                <FormControlLabel
                  value="provider"
                  control={<Radio />}
                  label={
                    <Box sx={{ ml: 1 }}>
                      <Stack direction="row" alignItems="center" spacing={1}>
                        <Business />
                        <Typography variant="h6">Provider</Typography>
                      </Stack>
                      <Typography variant="body2" color="text.secondary">
                        Offri i tuoi servizi e gestisci prenotazioni
                      </Typography>
                    </Box>
                  }
                />
              </Paper>
            </RadioGroup>
          </Box>
        );

      case 1:
        return (
          <Box>
            <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>
              Dati personali
            </Typography>
            <Stack spacing={3}>
              <TextField
                fullWidth
                label="Nome completo"
                value={formData.full_name}
                onChange={(e) => setFormData({ ...formData, full_name: e.target.value })}
                required
              />
              <TextField
                fullWidth
                label="Telefono"
                value={formData.phone}
                onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                placeholder="+39 333 1234567"
                required
              />
            </Stack>
          </Box>
        );

      case 2:
        if (userType === 'customer') {
          return (
            <Box>
              <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>
                Preferenze
              </Typography>
              <Typography variant="body1" color="text.secondary" sx={{ mb: 2 }}>
                Seleziona i servizi che ti interessano
              </Typography>
              <Stack direction="row" spacing={1} flexWrap="wrap">
                {['Ristoranti', 'Parrucchieri', 'Medici', 'Shopping', 'Casa', 'Sport'].map((pref) => (
                  <Chip
                    key={pref}
                    label={pref}
                    onClick={() => {
                      const prefs = formData.preferences;
                      if (prefs.includes(pref)) {
                        setFormData({
                          ...formData,
                          preferences: prefs.filter(p => p !== pref)
                        });
                      } else {
                        setFormData({
                          ...formData,
                          preferences: [...prefs, pref]
                        });
                      }
                    }}
                    color={formData.preferences.includes(pref) ? 'primary' : 'default'}
                    sx={{ mb: 1 }}
                  />
                ))}
              </Stack>
            </Box>
          );
        } else {
          return (
            <Box>
              <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>
                Dettagli attività
              </Typography>
              <Stack spacing={3}>
                <TextField
                  fullWidth
                  label="Nome attività"
                  value={formData.business_name}
                  onChange={(e) => setFormData({ ...formData, business_name: e.target.value })}
                  required
                />

                <FormControl fullWidth required>
                  <InputLabel>Categoria servizio</InputLabel>
                  <Select
                    value={formData.service_category}
                    onChange={(e) => setFormData({ ...formData, service_category: e.target.value })}
                    label="Categoria servizio"
                  >
                    {serviceCategories.map((cat) => (
                      <MenuItem key={cat.value} value={cat.value}>
                        {cat.label}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>

                <TextField
                  fullWidth
                  multiline
                  rows={3}
                  label="Descrizione attività"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                />

                <Typography variant="h6">Indirizzo attività</Typography>

                <Grid container spacing={2}>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Via/Piazza"
                      value={formData.address.street}
                      onChange={(e) => setFormData({
                        ...formData,
                        address: { ...formData.address, street: e.target.value }
                      })}
                      required
                    />
                  </Grid>
                  <Grid item xs={6}>
                    <TextField
                      fullWidth
                      label="Città"
                      value={formData.address.city}
                      onChange={(e) => setFormData({
                        ...formData,
                        address: { ...formData.address, city: e.target.value }
                      })}
                      required
                    />
                  </Grid>
                  <Grid item xs={3}>
                    <TextField
                      fullWidth
                      label="CAP"
                      value={formData.address.postal_code}
                      onChange={(e) => setFormData({
                        ...formData,
                        address: { ...formData.address, postal_code: e.target.value }
                      })}
                      required
                    />
                  </Grid>
                  <Grid item xs={3}>
                    <TextField
                      fullWidth
                      label="Provincia"
                      value={formData.address.province}
                      onChange={(e) => setFormData({
                        ...formData,
                        address: { ...formData.address, province: e.target.value }
                      })}
                      required
                    />
                  </Grid>
                </Grid>
              </Stack>
            </Box>
          );
        }

      case 3:
        return (
          <Box>
            <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>
              Riepilogo
            </Typography>

            <Stack spacing={2}>
              <Paper sx={{ p: 2, bgcolor: 'grey.50' }}>
                <Typography variant="subtitle2" color="text.secondary">
                  Tipo account
                </Typography>
                <Typography variant="body1">
                  {userType === 'customer' ? 'Cliente' : 'Provider'}
                </Typography>
              </Paper>

              <Paper sx={{ p: 2, bgcolor: 'grey.50' }}>
                <Typography variant="subtitle2" color="text.secondary">
                  Nome
                </Typography>
                <Typography variant="body1">
                  {formData.full_name}
                </Typography>
              </Paper>

              <Paper sx={{ p: 2, bgcolor: 'grey.50' }}>
                <Typography variant="subtitle2" color="text.secondary">
                  Telefono
                </Typography>
                <Typography variant="body1">
                  {formData.phone}
                </Typography>
              </Paper>

              {userType === 'provider' && (
                <>
                  <Paper sx={{ p: 2, bgcolor: 'grey.50' }}>
                    <Typography variant="subtitle2" color="text.secondary">
                      Attività
                    </Typography>
                    <Typography variant="body1">
                      {formData.business_name}
                    </Typography>
                  </Paper>

                  <Paper sx={{ p: 2, bgcolor: 'grey.50' }}>
                    <Typography variant="subtitle2" color="text.secondary">
                      Indirizzo
                    </Typography>
                    <Typography variant="body1">
                      {formData.address.street}, {formData.address.city} {formData.address.postal_code}
                    </Typography>
                  </Paper>
                </>
              )}
            </Stack>

            <Alert severity="info" sx={{ mt: 3 }}>
              Cliccando su "Completa registrazione" accetti i nostri Termini di Servizio e la Privacy Policy
            </Alert>
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        py: 4,
      }}
    >
      <Container maxWidth="md">
        <Box sx={{ mb: 2 }}>
          <IconButton onClick={() => navigate('/')} sx={{ color: 'white' }}>
            <ArrowBack />
          </IconButton>
          <Typography component="span" sx={{ color: 'white', ml: 1 }}>
            Torna alla home
          </Typography>
        </Box>

        <Paper elevation={24} sx={{ p: 4, borderRadius: 2 }}>
          <Typography variant="h4" align="center" sx={{ mb: 1, fontWeight: 600 }}>
            Registrati su commIT
          </Typography>
          <Typography variant="body1" align="center" color="text.secondary" sx={{ mb: 4 }}>
            Completa la registrazione in pochi passaggi
          </Typography>

          <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}

          {renderStepContent()}

          <Stack direction="row" spacing={2} sx={{ mt: 4 }}>
            <Button
              disabled={activeStep === 0}
              onClick={handleBack}
            >
              Indietro
            </Button>
            <Box sx={{ flex: 1 }} />
            <Button
              variant="contained"
              onClick={handleNext}
              disabled={loading}
              endIcon={activeStep === steps.length - 1 ? <Check /> : <ArrowForward />}
            >
              {activeStep === steps.length - 1 ? 'Completa registrazione' : 'Avanti'}
            </Button>
          </Stack>
        </Paper>
      </Container>
    </Box>
  );
};

export default Register;
